code = ''


def get_adjacent():
    adjacent = {6: (2, 9, 3), 2: (6, 4, 9, 3, 1), 4: (2, 3, 1), 9: (6, 2, 3, 8, 7), 3: (6, 2, 4, 9, 1, 7, 8, 5),
                1: (4, 2, 3, 8, 5), 7: (9, 3, 8), 8: (7, 9, 3, 1, 5), 5: (1, 3, 8)}
    return adjacent


traversed_code = [code[0]]
time = 0
adjacent = get_adjacent()

if __name__ == '__main__':
    for i in range(1, len(code)):
        try:
            prev = int(traversed_code[i - 1])
            current = int(code[i])

            if prev == current:
                pass
            elif current in adjacent[prev]:
                time += 1
            else:
                time += 2

            traversed_code.append(code[i])
        except Exception as e:

            print(e)
            break

    print(time)
